        "salaOSeccion": "S - Sala de Definición de Situaciones Jurídicas",
        "nombreDecision": "Resolución_SDSJ-3731_19-julio-2019",
        "procedimiento": "REVISIÓN Y SUPERVISIÓN DE BENEFICIOS",
        "expediente": "",
        "departamento": "DEPARTAMENTO TOLIMA",
        "departamentoNombre": "DEPARTAMENTO TOLIMA",
